mapper.delete:
update sys_menu
set is_delete = '1'
where menu_id = #{menuId}

select.where:

    and menu_name like concat('%',#{menuName},'%')


    and status = #{status}


    and visible = #{visible}

and is_delete = '0'
order by order_num

mapper.select:
select distinct m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.`query`, m.visible, ifnull(perms, '') as perms, m.is_frame, m.is_cache, m.menu_type, m.icon, m.order_num, m.create_time from sys_menu m left join sys_role_menu rm on rm.menu_id = m.menu_id left join sys_user_role ur on ur.role_id = rm.role_id left join sys_role r on r.role_id = ur.role_id where ur.user_id = #{params.userId}
and menu_name like concat('%',#{menuName},'%')
and status = #{status}
and visible = #{visible}
and m.is_delete = '0' and r.is_delete = '0' order by m.order_num


where menu_name = #{menuName}

    and menu_id <> #{menuId}

and is_delete = '0'

select distinct menu_id
from sys_menu
where menu_id = #{menuId}
   or parent_id = #{menuId}
    and is_delete = '0'
